INSERT INTO games (image_url, name, description, rating, genre1, genre2, age_rating, duration, num_players, developer, comment1, comment2, mechanics, rules) VALUES
('dobble.png', 'Dobble', 'Jogo de cartas rapido onde os jogadores devem encontrar o simbolo em comum entre duas cartas.', 5, 'Cartas', 'Observacao', 6, 15, 8, 'Asmodee', 'Viciante! Otimo para jogar com amigos e familia.', 'Simples de aprender e perfeito para todas as idades.', 'Percepcao visual, velocidade e reconhecimento de padroes.', 'Encontre o simbolo em comum entre duas cartas. O mais rapido descarta sua carta. Vence quem ficar sem cartas primeiro.'),
('uno.png', 'UNO', 'Classico jogo de cartas em que o objetivo e ser o primeiro a se livrar de todas as cartas.', 5, 'Cartas', 'Estrategia', 7, 30, 10, 'Mattel', 'Diversao garantida para qualquer idade.', 'Um classico! Cuidado pra nao perder as amizades no caminho.', 'Gestao de mao, cartas especiais e compra de cartas.', 'Combine numero ou cor para descartar cartas. Use cartas especiais para atrapalhar os outros. Vence quem descartar todas as cartas primeiro.'),
('tgcqp.png', 'Taco Gato Cabra Queijo Pizza', 'Jogo de cartas rapido e caotico onde os jogadores precisam bater na carta correta no momento certo.', 4, 'Cartas', 'Festa', 7, 15, 8, 'Blue Orange Games', 'Rende muitas risadas e confusoes!', 'Perfeito para o grupo de amigos.', 'Ritmo, reflexo e associacao de palavras.', 'Os jogadores falam palavras em ordem ao virar cartas. Se coincidir, todos devem bater na pilha. O ultimo pega as cartas.'),
('dixit.png', 'Dixit', 'Jogo criativo e visual onde os jogadores usam pistas para adivinhar cartas ilustradas.', 5, 'Tabuleiro', 'Criatividade', 8, 40, 6, 'Libellud', 'As ilustracoes sao lindas.', 'Perfeito para quem gosta de usar a criatividade.', 'Associacao de ideias, deducao e narrativa.', 'Um jogador da uma pista sobre sua carta. Os outros escolhem cartas semelhantes. Todos votam na carta do narrador.'),
('trio.png', 'Trio', 'Jogo de cartas estrategico em que o objetivo e formar trios de numeros.', 4, 'Cartas', 'Estrategia', 7, 20, 6, 'Repos Production', 'Facil de aprender e muito competitivo!', 'Ideal para quem gosta de desafios rapidos.', 'Gestao de mao e coleta de conjuntos.', 'Os jogadores revelam cartas e tentam formar trios numericos. Vence quem formar mais trios.'),
('jenga.png', 'Jenga', 'Jogo de habilidade e equilibrio onde os jogadores retiram blocos de madeira de uma torre.', 5, 'Habilidade', 'Classico', 6, 15, 8, 'Hasbro', 'Tensao e diversao a cada jogada!', 'Um classico indispensavel para qualquer reuniao.', 'Destreza manual e equilibrio.', 'Cada jogador retira um bloco e coloca no topo da torre. Quem derrubar a torre perde.'),
('coup.png', 'Coup', 'Jogo de blefe e estrategia onde os jogadores assumem papeis secretos.', 4, 'Cartas', 'Estrategia', 10, 15, 6, 'Indie Boards & Cards', 'Adoro o aspecto de blefe do jogo.', 'Rapido e cheio de reviravoltas.', 'Blefe, deducao e eliminacao.', 'Use cartas de influencia para acoes. Pode blefar, mas pode ser desafiado. O ultimo jogador com influencia vence.'),
('perfilexpress.png', 'Perfil Express', 'Jogo de perguntas rapidas onde os jogadores tentam adivinhar respostas com base em perfis.', 4, 'Perguntas', 'Social', 12, 8, 6, 'Cranio Creations', 'Otimo para conhecer melhor os amigos!', 'Perfeito para festas e encontros sociais.', 'Trivia e deducao.', 'Um jogador le pistas de um perfil. Os outros tentam adivinhar o que e. Ganha quem acertar mais.'),
('catan.png', 'Catan', 'Jogo de estrategia onde os jogadores colonizam uma ilha, coletam recursos e negociam.', 5, 'Tabuleiro', 'Estrategia', 10, 90, 4, 'Kosmos', 'Um dos melhores jogos de estrategia.', 'Repleto de negociacoes e decisoes estrategicas.', 'Alocacao de recursos, negociacao e construcao.', 'Construa estradas e aldeias com recursos obtidos. Troque com outros jogadores. Vence quem alcancar 10 pontos de vitoria.'),
('imagemeacao.png', 'Imagem e Acao', 'Jogo classico onde os jogadores desenham ou atuam palavras para que a equipe adivinhe.', 4, 'Desenho', 'Festa', 8, 60, 16, 'Mattel', 'Diversao garantida para todas as idades!', 'Perfeito para festas em familia ou com amigos.', 'Desenho, mimica e adivinhacao.', 'Um jogador desenha ou atua sem falar. Sua equipe deve adivinhar a palavra dentro do tempo.'),
