INSERT INTO games (image_url, name, description, rating, genre1, genre2, age_rating, duration, num_players, developer, comment1, comment2, mechanics, rules) VALUES
('dobble.png', 'Dobble', 'Jogo de cartas rápido onde os jogadores devem encontrar o símbolo em comum entre duas cartas.', 5, 'Cartas', 'Observação', 6, 15, 8, 'Asmodee', 'Viciante! Ótimo para jogar com amigos e família.', 'Simples de aprender e perfeito para todas as idades.', 'Percepção visual, velocidade e reconhecimento de padrões.', 'Encontre o símbolo em comum entre duas cartas. O mais rápido descarta sua carta. Vence quem ficar sem cartas primeiro.'),
('uno.png', 'UNO', 'Clássico jogo de cartas em que o objetivo é ser o primeiro a se livrar de todas as cartas.', 5, 'Cartas', 'Estratégia', 7, 30, 10, 'Mattel', 'Diversão garantida para qualquer idade.', 'Um clássico! Cuidado pra não perder as amizades no caminho.', 'Gestão de mão, cartas especiais e compra de cartas.', 'Combine número ou cor para descartar cartas. Use cartas especiais para atrapalhar os outros. Vence quem descartar todas as cartas primeiro.'),
('tgcqp.png', 'Taco Gato Cabra Queijo Pizza', 'Jogo de cartas rápido e caótico onde os jogadores precisam bater na carta correta no momento certo.', 4, 'Cartas', 'Festa', 7, 15, 8, 'Blue Orange Games', 'Rende muitas risadas e confusões!', 'Perfeito para o grupo de amigos.', 'Ritmo, reflexo e associação de palavras.', 'Os jogadores falam palavras em ordem ao virar cartas. Se coincidir, todos devem bater na pilha. O último pega as cartas.'),
('dixit.png', 'Dixit', 'Jogo criativo e visual onde os jogadores usam pistas para adivinhar cartas ilustradas.', 5, 'Tabuleiro', 'Criatividade', 8, 40, 6, 'Libellud', 'As ilustrações são lindas.', 'Perfeito para quem gosta de usar a criatividade.', 'Associação de ideias, dedução e narrativa.', 'Um jogador dá uma pista sobre sua carta. Os outros escolhem cartas semelhantes. Todos votam na carta do narrador.'),
('trio.png', 'Trio', 'Jogo de cartas estratégico em que o objetivo é formar trios de números.', 4, 'Cartas', 'Estratégia', 7, 20, 6, 'Repos Production', 'Fácil de aprender e muito competitivo!', 'Ideal para quem gosta de desafios rápidos.', 'Gestão de mão e coleta de conjuntos.', 'Os jogadores revelam cartas e tentam formar trios numéricos. Vence quem formar mais trios.'),
('jenga.png', 'Jenga', 'Jogo de habilidade e equilíbrio onde os jogadores retiram blocos de madeira de uma torre.', 5, 'Habilidade', 'Clássico', 6, 15, 8, 'Hasbro', 'Tensão e diversão a cada jogada!', 'Um clássico indispensável para qualquer reunião.', 'Destreza manual e equilíbrio.', 'Cada jogador retira um bloco e coloca no topo da torre. Quem derrubar a torre perde.'),
('coup.png', 'Coup', 'Jogo de blefe e estratégia onde os jogadores assumem papéis secretos.', 4, 'Cartas', 'Estratégia', 10, 15, 6, 'Indie Boards & Cards', 'Adoro o aspecto de blefe do jogo.', 'Rápido e cheio de reviravoltas.', 'Blefe, dedução e eliminação.', 'Use cartas de influência para ações. Pode blefar, mas pode ser desafiado. O último jogador com influência vence.'),
('perfilexpress.png', 'Perfil Express', 'Jogo de perguntas rápidas onde os jogadores tentam adivinhar respostas com base em perfis.', 4, 'Perguntas', 'Social', 12, 8, 6, 'Cranio Creations', 'Ótimo para conhecer melhor os amigos!', 'Perfeito para festas e encontros sociais.', 'Trivia e dedução.', 'Um jogador lê pistas de um perfil. Os outros tentam adivinhar o que é. Ganha quem acertar mais.'),
('catan.png', 'Catan', 'Jogo de estratégia onde os jogadores colonizam uma ilha, coletam recursos e negociam.', 5, 'Tabuleiro', 'Estratégia', 10, 90, 4, 'Kosmos', 'Um dos melhores jogos de estratégia.', 'Repleto de negociações e decisões estratégicas.', 'Alocação de recursos, negociação e construção.', 'Construa estradas e aldeias com recursos obtidos. Troque com outros jogadores. Vence quem alcançar 10 pontos de vitória.'),
('imagemeacao.png', 'Imagem e Ação', 'Jogo clássico onde os jogadores desenham ou atuam palavras para que a equipe adivinhe.', 4, 'Desenho', 'Festa', 8, 60, 16, 'Mattel', 'Diversão garantida para todas as idades!', 'Perfeito para festas em família ou com amigos.', 'Desenho, mímica e adivinhação.', 'Um jogador desenha ou atua sem falar. Sua equipe deve adivinhar a palavra dentro do tempo.'),
('war.png', 'War', 'Jogo de estratégia militar onde os jogadores conquistam territórios.', 4, 'Tabuleiro', 'Estratégia', 10, 120, 6, 'Grow', 'Adoro a combinação de sorte e estratégia.', 'Um clássico que nunca sai de moda.', 'Controle de área, rolagem de dados e objetivos secretos.', 'Movimente exércitos e ataque territórios vizinhos com dados. Vence quem cumprir seu objetivo secreto.'),
('storycubes.png', 'Story Cubes', 'Jogo criativo onde os jogadores lançam dados com imagens e criam histórias.', 5, 'Dados', 'Criatividade', 6, 30, 12, 'Rorys Story Cubes', 'Estimula a imaginação.', 'Perfeito para todas as idades e ocasiões.', 'Narrativa e improvisação.', 'Role os dados e crie uma história usando as imagens sorteadas.'),
('hamburgueriamaluca.png', 'Hamburgueria Maluca', 'Jogo de cartas onde os jogadores competem para montar hambúrgueres malucos.', 4, 'Cartas', 'Festa', 7, 20, 6, 'Red Glove', 'Divertido e rápido de jogar!', 'Ideal para quem gosta de jogos leves e engraçados.', 'Gestão de mão e montagem de combinações.', 'Monte hambúrgueres com cartas sorteadas. Ganha quem completar mais pedidos corretos.'),
('codigosecreto.png', 'Código Secreto', 'Jogo de palavras onde os jogadores dão pistas para adivinhar as palavras corretas.', 5, 'Palavras', 'Estratégia', 10, 15, 8, 'Czech Games Edition', 'Ótimo para exercitar o cérebro!', 'Perfeito para grupos que gostam de desafios mentais.', 'Associação de palavras e dedução.', 'Um mestre dá pistas de uma palavra. Sua equipe tenta adivinhar quais palavras da grade são corretas sem acertar a palavra proibida.'),
('lince.png', 'Lince', 'Jogo de observação rápida onde os jogadores competem para encontrar objetos escondidos.', 4, 'Observação', 'Família', 6, 15, 4, 'Devir', 'Rápido e divertido para todas as idades.', 'Excelente para desenvolver atenção e rapidez.', 'Reconhecimento visual e rapidez.', 'Revele uma ficha e encontre o objeto correspondente no tabuleiro o mais rápido possível.'),
('monopoly.png', 'Monopoly', 'Clássico jogo de compra e venda de propriedades.', 4, 'Tabuleiro', 'Estratégia', 8, 120, 6, 'Hasbro', 'Um clássico que nunca sai de moda.', 'Ideal para longas sessões de jogo com amigos e família.', 'Gestão financeira, sorte e negociação.', 'Os jogadores compram, vendem e negociam propriedades. Cobra-se aluguel. O último jogador não falido vence.'),
('tickettoride.png', 'Ticket to Ride', 'Jogo de estratégia onde os jogadores coletam cartas de trem para construir rotas.', 5, 'Tabuleiro', 'Estratégia', 8, 60, 5, 'Days of Wonder', 'Fácil de aprender e muito estratégico.', 'Perfeito para todas as idades e níveis de experiência.', 'Coleção de cartas e construção de rotas.', 'Use cartas de trens para completar rotas no mapa. Vence quem acumular mais pontos com rotas e objetivos.'),
('sixtix.png', 'Sixtix', 'Jogo de cartas onde os jogadores tentam formar sequências numéricas.', 4, 'Cartas', 'Estratégia', 7, 20, 6, 'Lúdilo', 'Rápido e cheio de estratégia!', 'Ideal para quem gosta de jogos de cartas desafiadores.', 'Gestão de mão e sequências numéricas.', 'Jogue cartas para formar fileiras numéricas crescentes. Vence quem ficar sem cartas primeiro.'),
('tokaido.png', 'Tokaido', 'Jogo de tabuleiro onde os jogadores viajam pela estrada Tokaido no Japão.', 5, 'Tabuleiro', 'Aventura', 8, 60, 5, 'Funforge', 'Experiência relaxante e bonita.', 'Perfeito para quem gosta de jogos tranquilos e estratégicos.', 'Coleção de conjuntos e escolhas estratégicas.', 'Avance pela estrada escolhendo paradas turísticas. Vence quem acumular mais pontos de viagem.'),
('stella.png', 'Stella', 'Jogo de cartas onde os jogadores competem para coletar estrelas e completar objetivos.', 4, 'Cartas', 'Estratégia', 8, 30, 6, 'Lúdilo', 'Divertido e estratégico!', 'Ideal para quem gosta de jogos rápidos e desafiadores.', 'Associação de ideias e seleção simultânea.', 'Os jogadores associam palavras a imagens de cartas. Pontuam ao combinar escolhas com os outros.'),
('micromacro.png', 'MicroMacro: Crime City', 'Jogo de tabuleiro onde os jogadores investigam cenas de crime detalhadas.', 5, 'Tabuleiro', 'Investigação', 10, 60, 4, 'Pegasus Spiele', 'Incrivelmente detalhado e envolvente.', 'Perfeito para fãs de mistério e investigação.', 'Investigação visual e dedução.', 'Analise o mapa gigante para encontrar pistas e resolver casos criminais.'),
('ishtar.png', 'Ishtar', 'Jogo de tabuleiro onde os jogadores constroem e desenvolvem a cidade de Ishtar.', 4, 'Tabuleiro', 'Estratégia', 10, 90, 4, 'Lúdilo', 'Desafiador e muito estratégico!', 'Ideal para quem gosta de jogos complexos e envolventes.', 'Colocação de peças e controle de área.', 'Use recursos para plantar jardins e expandir sua influência. Vence quem acumular mais pontos.'),
('stickup.png', 'Stick Up!', 'Jogo de cartas onde os jogadores assumem o papel de ladrões tentando roubar joias.', 4, 'Cartas', 'Estratégia', 8, 20, 6, 'Red Glove', 'Divertido e cheio de reviravoltas!', 'Perfeito para quem gosta de jogos rápidos e estratégicos.', 'Gestão de mão e blefe.', 'Os jogadores jogam cartas secretamente para tentar roubar joias. Quem tiver a melhor jogada vence a rodada.'),
('noobjects.png', 'No Objects', 'Jogo de cartas onde os jogadores tentam adivinhar objetos com base em dicas.', 4, 'Cartas', 'Social', 7, 30, 8, 'Cranio Creations', 'Ótimo para conhecer melhor os amigos!', 'Perfeito para festas e encontros sociais.', 'Dedução e comunicação.', 'Um jogador dá dicas para que os outros adivinhem o objeto secreto. Quem acertar marca pontos.'),
('duna.png', 'Duna: Imperium', 'Jogo de tabuleiro estratégico baseado no universo de Duna.', 5, 'Tabuleiro', 'Estratégia', 12, 90, 4, 'Dire Wolf Digital', 'Incrível para fãs de Duna e jogos de estratégia.', 'Complexo e cheio de decisões táticas.', 'Construção de baralho e colocação de trabalhadores.', 'Use cartas para alocar agentes em locais estratégicos. Vence quem acumular 10 pontos de vitória.'),
('passpass.png', 'Pass Pass', 'Jogo de cartas estratégico onde os jogadores competem jogando cartas para conquistar pontos.', 3, 'Cartas', 'Familiar', 8, 25, 3, 'PaperGames', 'Caixa resistente à abertura e fechamento constantes.', 'Caixa compacta, facilitando o transporte; materiais de alta qualidade.', 'Gestão de mão e contagem de pontos.', 'Jogue cartas em disputas rápidas. O jogador com mais pontos no final vence.'),
('forsale.png', 'For Sale', 'Jogo de cartas rápido e estratégico onde os jogadores competem para comprar e vender propriedades.', 4, 'Cartas', 'Estratégia', 8, 30, 6, 'Lúdilo', 'Jogo com regras simples e partidas rápidas.', 'Componentes de alta qualidade e ótima experiência de jogo.', 'Leilão e gestão de recursos.', 'Na primeira fase compre propriedades em leilões. Na segunda, venda-as para obter mais lucro.'),
('dive.png', 'Dive', 'Jogo de cartas estratégico onde os jogadores assumem o papel de mergulhadores em busca de tesouros.', 3, 'Cartas', 'Estratégia', 10, 30, 4, 'Lúdilo', 'Jogo com regras fáceis e partidas dinâmicas.', 'Componentes de alta qualidade e ótima experiência de jogo.', 'Apostas e observação.', 'Os jogadores apostam em níveis de mergulho revelando camadas transparentes. Pontuam se acertarem.'),
('spyfall.png', 'Spyfall', 'Jogo de cartas social e de dedução onde os jogadores assumem papéis secretos em diferentes locais.', 4, 'Cartas', 'Social', 10, 15, 8, 'Cryptozoic Entertainment', 'Jogo fácil de aprender com muita interação.', 'Cartas de alta qualidade e ótima experiência de jogo.', 'Dedução social e blefe.', 'Um jogador é o espião sem saber o local. Os outros fazem perguntas tentando revelá-lo. O espião vence se adivinhar o local.'),
('stuffedfables.png', 'Stuffed Fables', 'Jogo de tabuleiro cooperativo onde os jogadores assumem o papel de brinquedos que ganham vida.', 3, 'Tabuleiro', 'Aventura', 8, 60, 4, 'Plaid Hat Games', 'Componentes de alta qualidade com miniaturas detalhadas.', 'Narrativa envolvente e experiência cooperativa.', 'Cooperativo baseado em história.', 'Leia o livro-jogo e complete missões em equipe. As escolhas afetam o desenrolar da narrativa.'),
('junglespeed.png', 'Jungle Speed', 'Jogo de cartas rápido onde os jogadores competem para pegar o totem quando cartas iguais são reveladas.', 3, 'Cartas', 'Festa', 7, 15, 10, 'Asmodee', 'Jogo fácil de aprender com muita ação.', 'Componentes duráveis e ótima experiência de jogo.', 'Reflexo e reconhecimento de padrões.', 'Revele cartas em sequência. Se duas coincidirem, os jogadores envolvidos correm para pegar o totem.'),
('team3.png', 'Team 3', 'Jogo de cartas cooperativo onde os jogadores trabalham juntos para completar desafios.', 3, 'Cartas', 'Cooperativo', 8, 30, 6, 'Lúdilo', 'Jogo com regras simples e partidas dinâmicas.', 'Componentes de alta qualidade e ótima experiência de jogo.', 'Cooperação e comunicação.', 'Três jogadores assumem papéis diferentes (um não fala, outro não vê, outro constrói) para completar construções.'),
('foideproposito.png', 'Foi de Propósito', 'Jogo de cartas estratégico onde os jogadores competem para completar objetivos secretos.', 5, 'Cartas', 'Estratégia', 10, 30, 6, 'Red Glove', 'Jogo fácil de aprender com muita interação.', 'Cartas de alta qualidade e ótima experiência de jogo.', 'Gestão de mão e objetivos secretos.', 'Cada jogador recebe objetivos e tenta cumpri-los em segredo.'),
('theresistance.png', 'The Resistance', 'Jogo de cartas social e de dedução onde os jogadores assumem papéis secretos.', 5, 'Cartas', 'Social', 10, 30, 10, 'Indie Boards & Cards', 'Jogo fácil de aprender com muita interação.', 'Cartas de alta qualidade e ótima experiência de jogo.', 'Dedução social e blefe.', 'Jogadores são rebeldes ou espiões. Rebeldes tentam completar missões enquanto espiões tentam sabotá-las.'),
('master.png', 'Master', 'Jogo de cartas estratégico onde os jogadores competem para se tornar o mestre do jogo.', 5, 'Cartas', 'Estratégia', 10, 30, 6, 'Lúdilo', 'Jogo com regras simples e partidas dinâmicas.', 'Componentes de alta qualidade e ótima experiência de jogo.', 'Gestão de mão e controle de área.', 'Jogue cartas para dominar territórios e acumular pontos.'),
('imagine.png', 'Imagine', 'Jogo de cartas criativo onde os jogadores usam transparências com símbolos para representar palavras.', 3, 'Cartas', 'Criatividade', 8, 30, 6, 'Repos Production', 'Jogo fácil de aprender com muita criatividade.', 'Componentes de alta qualidade e ótima experiência de jogo.', 'Mecânica de adivinhação, criatividade e comunicação.', 'Os jogadores utilizam cartas transparentes com símbolos para representar palavras ou frases. Os outros devem adivinhar o que está sendo mostrado. Vence quem acumular mais acertos.'),
('loveletter.png', 'Love Letter', 'Jogo de cartas rápido e estratégico onde os jogadores competem para entregar uma carta de amor à princesa.', 3, 'Cartas', 'Estratégia', 8, 20, 4, 'Cranio Creations', 'Jogo fácil de aprender com muita interação.', 'Cartas de alta qualidade e ótima experiência de jogo.', 'Mecânica de blefe, dedução e eliminação.', 'Cada jogador mantém uma carta na mão e compra outra em seu turno, jogando uma delas. Cada carta tem efeitos especiais que podem eliminar adversários ou proteger o jogador. Vence quem permanecer por último ou tiver a carta mais alta na rodada.'),
('misty.png', 'Misty', 'Jogo de cartas estratégico onde os jogadores competem para controlar territórios e acumular pontos.', 4, 'Cartas', 'Estratégia', 10, 30, 6, 'Lúdilo', 'Jogo com regras simples e partidas dinâmicas.', 'Componentes de alta qualidade e ótima experiência de jogo.', 'Mecânica de draft de cartas e controle de área.', 'Os jogadores constroem um mural com cartas que interagem entre si. Algumas cartas se movem ou se sobrepõem, criando estratégias inesperadas. Ao final, contam-se os pontos de acordo com as combinações formadas.'),
('godofwar.png', 'God of War: O Jogo de Tabuleiro', 'Adaptação do videogame, onde os jogadores assumem o papel de Kratos e Atreus em uma jornada épica.', 5, 'Tabuleiro', 'Aventura', 12, 90, 4, 'CMON Limited', 'Componentes de alta qualidade com miniaturas detalhadas.', 'Narrativa envolvente e experiência cooperativa.', 'Mecânica cooperativa, gerenciamento de recursos e combate contra chefes.', 'Os jogadores exploram cenários representados por cartas, enfrentam inimigos e chefes poderosos utilizando cartas de habilidades. A vitória depende do trabalho em equipe para superar os desafios da narrativa.'),
('gameoflife.png', 'Game of Life', 'Jogo de tabuleiro clássico onde os jogadores simulam a jornada da vida.', 5, 'Tabuleiro', 'Família', 8, 60, 6, 'Hasbro', 'Um clássico que nunca sai de moda.', 'Ideal para longas sessões de jogo com amigos e família.', 'Mecânica de rolagem de dados/roda da sorte, progressão linear e escolhas estratégicas.', 'Cada jogador segue pelo tabuleiro tomando decisões como carreira, família e investimentos. O objetivo é acumular riqueza e felicidade até o fim do jogo. Vence quem tiver o maior saldo final.'),
('sherlock.png', 'Sherlock Express', 'Jogo de cartas rápido onde os jogadores assumem o papel de detetives tentando capturar Moriarty.', 4, 'Cartas', 'Mistério', 8, 20, 6, 'Devir', 'Jogo fácil de aprender com muita interação.', 'Cartas de alta qualidade e ótima experiência de jogo.', 'Mecânica de dedução rápida e reflexo.', 'Os jogadores analisam pistas para eliminar suspeitos. Quando sobra apenas um culpado, todos devem bater rapidamente na carta correspondente. Quem for mais rápido marca pontos.'),
('combocolor.png', 'Combo Color', 'Jogo de cartas rápido e estratégico onde os jogadores competem para formar combinações de cores e números.', 3, 'Cartas', 'Estratégia', 8, 20, 6, 'Lúdilo', 'Jogo com regras simples e partidas dinâmicas.', 'Componentes de alta qualidade e ótima experiência de jogo.', 'Mecânica de combinação de cores, posicionamento e controle de área.', 'Os jogadores pintam territórios no tabuleiro ao posicionar cartas. A pontuação depende das cores mais presentes em cada área e dos símbolos coletados. Vence quem acumular mais pontos ao final.'),
('castleparty.png', 'Castle Party', 'Jogo de tabuleiro estratégico onde os jogadores competem para construir e decorar o castelo.', 4, 'Tabuleiro', 'Estratégia', 8, 60, 4, 'Lúdilo', 'Jogo com regras simples e partidas dinâmicas.', 'Componentes de alta qualidade e ótima experiência de jogo.', 'Mecânica de colocação de peças e quebra-cabeça.', 'Os jogadores organizam convidados monstruosos em um baile no castelo utilizando formas de cartas que lembram Tetris. Ao final, a pontuação depende da organização e das condições cumpridas.'),
('tinytowns.png', 'Tiny Towns', 'Jogo de tabuleiro estratégico onde os jogadores assumem o papel de prefeitos de pequenas cidades.', 4, 'Tabuleiro', 'Estratégia', 10, 45, 4, 'Alderac Entertainment Group', 'Componentes de alta qualidade com peças detalhadas.', 'Regras fáceis de aprender e experiência fluida.', 'Mecânica de colocação de peças, construção de padrões e otimização de espaço.', 'A cada turno, um recurso é escolhido e todos os jogadores devem colocá-lo em sua grade de cidade. Quando formam combinações, constroem edifícios que rendem pontos. O jogo termina quando ninguém consegue mais posicionar recursos.');
