CREATE DATABASE gamelounge;

\c gamelounge;

CREATE TABLE games (
    id SERIAL PRIMARY KEY,
    image_url TEXT,
    name VARCHAR(100) NOT NULL,
    description TEXT,
    rating INT,
    genre1 VARCHAR(50),
    genre2 VARCHAR(50),
    age_rating INT,
    duration INT,
    num_players INT,
    developer VARCHAR(100),
    comment1 TEXT,
    comment2 TEXT,
    mechanics TEXT,
    rules TEXT
);

